 */
class DataProcessor {
    constructor() {
        this.data = [];
    }

    /**
     * Add an item to the data array
     * @param {any} item - Item to add
     */
    addItem(item) {
        this.data.push(item);
    }

    /**
     * Process all data items
     * @returns {Array} Processed data
     */
    processAll() {
        return this.data.map(item => item.toString().toUpperCase());
    }
}

