description = 'Gas Handling PLC'
group = 'lowlevel'

tango_base = 'tango://localhost:10000/dr/panel/'

devices = dict(
    flow = device('nicos.devices.entangle.Sensor',
        description = '3He flow',
        tangodevice = tango_base + 'flow',
        pollinterval = 0.7,
        maxage = 2,
        fmtstr = '%.2f',
        unit = "V",
    ),
    p_IVC = device('nicos.devices.entangle.Sensor',
        description = 'IVC pressure',
        tangodevice = tango_base + 'p_ivc',
        pollinterval = 0.7,
        maxage = 2,
        fmtstr = '%.6f',
        unit = "mbar",
    ),
    p_still = device('nicos.devices.entangle.Sensor',
        description = 'Still pressure',
        tangodevice = tango_base + 'p_still',
        pollinterval = 0.7,
        maxage = 2,
        fmtstr = '%.6f',
        unit = "mbar",
    ),
    p_he3b = device('nicos.devices.entangle.Sensor',
        description = '3He back pressure',
        tangodevice = tango_base + 'p_he3b',
        pollinterval = 0.7,
        maxage = 2,
        fmtstr = '%.1f',
        unit = "mbar",
    ),
    p_1K = device('nicos.devices.entangle.Sensor',
        description = '1K pot pressure',
        tangodevice = tango_base + 'p_1k',
        pollinterval = 5,
        maxage = 12,
        fmtstr = '%.0f',
        unit = "mbar",
    ),
    lhe_level = device('nicos.devices.entangle.Sensor',
        description = 'LHe level',
        tangodevice = tango_base + 'lhe',
        pollinterval = 10,
        maxage = 25,
        fmtstr = '%.1f',
        unit = "%",
    ),
)

