description = 'cctv1 MercuryiTC cryo controller.'

group = 'plugplay'

tango_base = f'tango://{setupname}:10000/box/itc/'

devices = {
    f't_vti': device('nicos.devices.entangle.TemperatureController',
        description = 'VTI temperature controller',
        tangodevice = tango_base + 't_vti',
        unit = 'K',
        fmtstr = '%.4f',
    ),
    'heater_vti': device('nicos.devices.entangle.AnalogOutput',
        description = 'VTI heater',
        tangodevice = tango_base + 'heater_vti',
        unit='%',
        fmtstr = '%.4f',
    ),
    f't_sample1': device('nicos.devices.entangle.TemperatureController',
        description = 'Sample1 temperature controller',
        tangodevice = tango_base + 't_sample1',
        unit = 'K',
        fmtstr = '%.4f',
    ),
    'heater_sample1': device('nicos.devices.entangle.AnalogOutput',
        description = 'Sample1 heater',
        tangodevice = tango_base + 'heater_sample1',
        unit='%',
        fmtstr = '%.4f',
    ),
    f't_pt2': device('nicos.devices.entangle.TemperatureController',
        description = 'PT2 temperature controller',
        tangodevice = tango_base + 't_pt2',
        unit = 'K',
        fmtstr = '%.4f',
    ),
    'heater_pt2': device('nicos.devices.entangle.AnalogOutput',
        description = 'PT2 heater',
        tangodevice = tango_base + 'heater_pt2',
        unit='%',
        fmtstr = '%.4f',
    ),
    f'p_vti': device('nicos.devices.entangle.TemperatureController',
        description = 'VTI pressure controller',
        tangodevice = tango_base + 'p_vti',
        unit = 'mbar',
        fmtstr = '%.4f',
    ),
    'needle_valve' : device('nicos.devices.entangle.AnalogOutput',
        description='Needle valve',
        tangodevice=tango_base + 'needle_valve',
        unit='%',
        fmtstr = '%.4f',
    ),
    f't_pt1': device('nicos.devices.entangle.Sensor',
        description = 'PT1 temperature sensor',
        tangodevice = tango_base + 't_pt1',
        unit = 'K',
        fmtstr = '%.4f',
    ),
    f't_sample2': device('nicos.devices.entangle.Sensor',
        description = 'Sample1 temperature sensor',
        tangodevice = tango_base + 't_sample2',
        unit = 'K',
        fmtstr = '%.4f',
    ),
}
