import os
import setuptools
from glob import glob


with open("README.md", "r", encoding="utf-8") as fh:
    long_description = fh.read()

QISKIT_BRIDGE_PKGS = ["qiskit~=2.1.2", "seaborn~=0.13"]
QUTIP_BRIDGE_PKGS = ["qutip~=5.0.4"]
MYQLM_BRIDGE_PKGS = ["myqlm~=1.11.3"]
CQASM_BRIDGE_PKGS = ["libqasm==1.2.1"]  # libqasm is not stable enough to put ~=

# Package list is autogenerated to be any 'perceval_interop' subfolder containing a __init__.py file
package_list = [os.path.dirname(p).replace('\\', '.') for p in glob('perceval_interop/**/__init__.py', recursive=True)]

setuptools.setup(
    name="perceval-interop",
    author="quandela",
    description="Interoperability packages between Perceval and other quantum computing frameworks",
    long_description=long_description,
    long_description_content_type="text/markdown",
    url="https://github.com/Quandela/Perceval_Interop",
    project_urls={
        "Documentation": "https://perceval.quandela.net/interopdocs/",
        "Source": "https://github.com/Quandela/Perceval_Interop",
        "Tracker": "https://github.com/Quandela/Perceval_Interop/issues"
    },
    classifiers=[
        "Programming Language :: Python :: 3.9",
        "Programming Language :: Python :: 3.10",
        "Programming Language :: Python :: 3.11",
        "Programming Language :: Python :: 3.12",
        "Programming Language :: Python :: 3.13",
        "License :: OSI Approved :: MIT License",
        "Operating System :: OS Independent",
    ],
    packages=package_list,
    install_requires=['perceval-quandela~=1.0.1'],
    extras_require={
        "qiskit_bridge": QISKIT_BRIDGE_PKGS,
        "qutip_bridge": QUTIP_BRIDGE_PKGS,
        "myqlm_bridge": MYQLM_BRIDGE_PKGS,
        "cqasm_bridge": CQASM_BRIDGE_PKGS,
        "all": QISKIT_BRIDGE_PKGS + QUTIP_BRIDGE_PKGS + MYQLM_BRIDGE_PKGS + CQASM_BRIDGE_PKGS
    },
    setup_requires=["scmver"],
    python_requires=">=3.9,<3.14",
    scmver=True
)
