## Unit conversions
# Length units
KG_TO_G = 1e3
G_TO_KG = 1e-3
M_TO_CM = 1e2
CM_TO_M = 1e-2
M_TO_MM = 1e3
MM_TO_M = 1e-3
M_TO_DM = 1e1
DM_TO_M = 1e-1
MG_TO_KG = 1e-6
KG_TO_MG = 1e6
M_TO_UM = 1e6
UM_TO_M = 1e-6
MM_TO_CM = 1e-1
CM_TO_MM = 1e1
UM_TO_MM = 1e-3
mG_TO_G = 1e-3
G_TO_mG = 1e3
CM_TO_UM = 1e4
UM_TO_CM = 1e-4

# Current units
A_TO_mA = 1e3
mA_TO_A = 1e-3

# Time units
S_TO_H = 1 / 3600
H_TO_S = 3600
S_TO_Y = 1 / (3600 * 24 * 365)
Y_TO_S = 3600 * 24 * 365

# Energy units
W_TO_KW = 1e-3

# Angle units
DEG_TO_RAD = 0.017453292519943295

# Percentage units
PERCENT_TO_FRACTION = 1e-2
FRACTION_TO_PERCENT = 1e2
